var TreasureBox=function(){this.init.call(this)};TreasureBox.prototype.init=function(){this._itemArr=["\\","$","&","#","!","%","^","|","*","@"];this._treasureCount=0;this._tresureBox=this._getBox(0)};TreasureBox.prototype.getTresureBox=function(){return this._tresureBox};TreasureBox.prototype.checkTresureCount=function(a){return this._treasureCount===a};TreasureBox.prototype._getBox=function(f){var d,c,e;var a=[];f=parseInt(f,10);for(var b=0;b<100;b++){d=10;if(f<5){d=11}c=Math.floor(Math.random()*d);if(c===10){e=this._getBox(f+1)}else{e=this._itemArr[c];if(e==="\\"||e==="$"){this._treasureCount++}}a.push(e)}return a};var Answer=function(){this.init.call(this)};Answer.prototype.init=function(){var b=new TreasureBox();var a=this.getTresuerCount(b.getTresureBox());if(!b.checkTresureCount(a)){alert("間違い！")}};Answer.prototype.log=function(a){var b=document.createElement("p");b.innerText=a;this.target.appendChild(b)};Answer.prototype.getTresuerCount=function(d){var b=0;for(var c=0,a=d.length;c<a;c++){if(Object.prototype.toString.call(d[c])==="[object Array]"){b+=this.getTresuerCount(d[c])}else{if(d[c]==="\\"||d[c]==="$"){b++}}}return b};for(var i=0;i<10;i++){new Answer()};